+				},
+				{
+					name:"水煮鱼",
+					price:88,
+					num:1,
+					id:2
+				}
+			],
+			[
+				{
+					name:"小炒日本豆腐",
+					price:18,
+					num:1,
+					id:3
+				},
+				{
+					name:"烤鱼",
+					price:58,
+					num:1,
+					id:4
+				}
+			],
+			[
+				{
+					name:"大拌菜",
+					price:18,
+					num:1,
+					id:5
+				},
+				{
+					name:"川北凉粉",
+					price:8,
+					num:1,
+					id:6
+				}
+			],
+			[]
+		],
+		dishes:[]
+	},
+	loadingChange () {
+		setTimeout(() => {
+			this.setData({
+				hidden:true
+			})
+		},2000)
+	},
+	selectNav (event) {
+		let id = event.target.dataset.id,
+			index = parseInt(event.target.dataset.index);
+			self = this;
+		this.setData({
+			curNav:id,
+			curIndex:index
+		})
+	},
+	// 选择菜品
+	selectDish (event) {
+		let dish = event.currentTarget.dataset.dish;
+		let flag = true;
+		let	cart = this.data.cart;
+		
+		if(cart.length > 0){
+			cart.forEach(function(item,index){
+				if(item == dish){
+					cart.splice(index,1);
+					flag = false;
+				}
+			})
+		}
+		if(flag) cart.push(dish);
+		this.setData({
+			cartTotal:cart.length
+		})
+		this.setStatus(dish)
+	},
+	setStatus (dishId) {
+		let dishes = this.data.dishesList;
+		for (let dish of dishes){
+			dish.forEach((item) => {
+				if(item.id == dishId){
+					item.status = !item.status || false
+				}
+			})
+		}
+		
+		this.setData({
+			dishesList:this.data.dishesList
+		})
+	},
+	onLoad () {
+		this.loadingChange()
+	}
+})
